from flask import Flask, jsonify
import os
import requests
from web3 import Web3

def getCoingeckoPrices():
    r = requests.get('https://api.coingecko.com/api/v3/simple/price?ids=weth%2Cnear&vs_currencies=usd')
    return r.json()

def getTokenPrice(address):
    url = 'https://pro-openapi.debank.com/v1/token?chain_id=aurora&id={}'.format(address)
    headers = {'accept': 'application/json', 'AccessKey': os.getenv('DEBANK_KEY')}
    r = requests.get(url, headers=headers)
    tokenInfo = r.json()
    return tokenInfo['price']

