app = Flask(__name__)

w3 = Web3(Web3.HTTPProvider('https://mainnet.aurora.dev'))
contractInstance = w3.eth.contract(address=contractAddress, abi=abi)

block = w3.eth.getBlock('latest')
blockNumber = block['number']
prices = getCoingeckoPrices()
multiplier = contractInstance.functions.getMultiplier(blockNumber, blockNumber + 1).call()


BRLPerBlock = contractInstance.functions.BRLPerBlock().call()
rewardsPerWeek = BRLPerBlock / 10**18 * multiplier * 604800 / 1.1
totalAllocPoints = contractInstance.functions.totalAllocPoint().call()
rewardTokenAddress = contractInstance.functions.BRL().call()
poolInfo = contractInstance.functions.poolInfo(1).call()
poolRewardsPerWeek = poolInfo[1] / totalAllocPoints * rewardsPerWeek
rewardPrice = getTokenPrice(rewardTokenAddress)
usdPerWeek = poolRewardsPerWeek * rewardPrice
pool = w3.eth.contract(address=poolInfo[0], abi=UNI_ABI)
decimals = pool.functions.decimals().call()
staked = pool.functions.balanceOf(contractAddress).call() / 10 ** decimals
price = getTokenPrice(poolInfo[0])

stakedTvl = staked * price

weeklyApr = usdPerWeek / stakedTvl * 100
dailyApr = weeklyApr / 7
yearlyApr = weeklyApr * 52

response = {'poolAddress': poolInfo[0], 'weeklyApr': weeklyApr, 'dailyApr': dailyApr, 'yearlyApr': yearlyApr}

@app.route("/")
def hello_world():
    return jsonify(response)

    